import React, {useRef} from 'react';

import {gsap} from 'gsap';

import classes from './hero.module.css';

function Hero(){

    const fieldRef = useRef([]);

    const addToRefs = (el) => {
        if (el && !fieldRef.current.includes(el)) {
            fieldRef.current.push(el);
        }
      };

    const tl = gsap.timeline();

    fieldRef.current.forEach( field => {
        gsap.set( field, {
            yPercent: -150
        } )

        tl.to( field, {
            yPercent: -50,
            stagger: 0.5,
            duration: 1,
            ease: "expo.inOut",
            yoyo: true,
            repeat: -1
        } )
    } )

    return (
        <section id="hero" className='full-page flex direction-column justify-center'>
            <div className="row">
                <div className="container">
                    <h5 className='txt-green txt-small txt-font-mono'>My name is</h5>
                    <h2 className="big-heading">Suhail Habib.</h2>
                    <h2 className='semi-heading txt-slate'>My mission is to maximize my clients' revenue.</h2>
                    <p className="mt-20">As a highly skilled full-stack and WordPress web developer, I offer a range of cutting-edge web development services to help my clients achieve their business goals. </p>
                    <p className="mt-20 flex align-center">
                        <span className='line-height-0'>Specializing in</span> 
                        <span className={classes.fields}>
                            <span className={classes.field} ref={addToRefs}>HTML</span>
                            <span className={classes.field} ref={addToRefs}>CSS</span>
                            <span className={classes.field} ref={addToRefs}>JavaScript</span>
                            <span className={classes.field} ref={addToRefs}>ReactJs</span>
                            <span className={classes.field} ref={addToRefs}>NodeJs</span>
                            <span className={classes.field} ref={addToRefs}>AWS</span>
                            <span className={classes.field} ref={addToRefs}>PYTHON</span>
                            <span className={classes.field} ref={addToRefs}>WordPress</span>
                        </span>
                    </p>
                </div>
            </div>
        </section>
    )
}

export default Hero;
