import React, {useState,useRef} from 'react'
import {gsap} from 'gsap';

import SectionTitle from './layout/SectionTitle';

import classes from "./Work.module.css";

const data = [
    {
        title: 'Fullstack developer',
        workAt: 'Cubix IT Limited',
        time: '2021 - Present',
        ul: [
            "Write modern, performant, maintainable code for a diverse array of client and internal projects",
            "Work with a variety of different languages, platforms, frameworks, and content management systems such as JavaScript, TypeScript, Gatsby, React, Craft, WordPress, Prismic, and Netlify",
            "Communicate with multi-disciplinary teams of engineers, designers, producers, and clients on a daily basis"
        ]

    }
]

function Work(){

    const barRef = useRef();
    const [itemNum, setItem] = useState(1);

    const handleClick = (e)=>{

        const parentPosition = e.currentTarget.parentNode.getBoundingClientRect().top;
        const childPosition = e.currentTarget.getBoundingClientRect().top;
        const position = childPosition-parentPosition;

        gsap.to(barRef.current, {
            top: position,
            ease: 'power4.out'
        })

        const item = e.currentTarget.dataset['item'];
        setItem(item);

    }

    return(
        <section id="work">
            <div className="container">
                <SectionTitle title="Where Iâ€™ve Worked" number="02" />
                <div className={classes['work-wrapper']+" mt-50"}>
                    <div className="work-nav">
                        <ul className={classes.lists}>
                            <span className={classes.bar} ref={barRef}></span>
                            <li className={classes.list} onClick={handleClick} data-item="1">Cubix IT Limited</li>
                            <li className={classes.list} onClick={handleClick} data-item="2">Spire Sparkle Agency</li>
                            <li className={classes.list} onClick={handleClick} data-item="3">Fiverr, Upwork</li>
                        </ul>
                    </div>
                    <div className="work-details">
                        <h2 className={classes.title}>Fullstack developer <span class="green">@Cubix IT Limited</span></h2>
                        <p className={classes['time-span']}>2021 - Present</p>
                        <div>
                            <ul className={classes['work-ul']}>
                                <li>Write modern, performant, maintainable code for a diverse array of client and internal projects</li>
                                <li>Work with a variety of different languages, platforms, frameworks, and content management systems such as JavaScript, TypeScript, Gatsby, React, Craft, WordPress, Prismic, and Netlify</li>
                                <li>Communicate with multi-disciplinary teams of engineers, designers, producers, and clients on a daily basis</li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default Work;
